import React from 'react';

class FieldDropdown extends React.Component {
  static propTypes = {
    label: React.PropTypes.string,
    options: React.PropTypes.object,
    error: React.PropTypes.string,
    id: React.PropTypes.string,
  };

  static defaultProps = {
    options: [],
  };

  render() {
    const { label, options, error, id, ...rest } = this.props;

    return (
      <div className="field-dropdown">
        {!!label ?
          <label className="field-dropdown__label" htmlFor={id}>{label}</label>
        : null}

        {!!error ?
          <span className="field-dropdown__error">{error}</span>
        : null}

        <select id={id} className="field-dropdown__field" {...rest}>
          {
            Object.keys(options).map((opt, index) =>
              <option key={index} value={opt}>{options[opt]}</option>
            )
          }
        </select>
      </div>
    );
  }
}

export default FieldDropdown;
