import React from 'react';

class FieldTextarea extends React.Component {
  static propTypes = {
    label: React.PropTypes.string,
    validation: React.PropTypes.string,
    id: React.PropTypes.string.isRequired,
    name: React.PropTypes.string.isRequired,
    type: React.PropTypes.string,
    value: React.PropTypes.string,
    error: React.PropTypes.string,
  };

  static defaultProps = {
    type: 'text',
    value: null,
  };

  render() {
    const { label, id, error, ...rest } = this.props;

    return (
       <div className="field-textarea">
        {!!label ?
          <label className="field-textarea__label" htmlFor={id}>{label}</label>
        : null}

        {!!error ?
          <span className="field-textarea__error">{error}</span>
        : null}

        <textarea
          className="field-textarea__field"
          rows="5"
          id={id}
          {...rest}
        />
      </div>
    );
  }
}

export default FieldTextarea;
