import React from 'react';

class FormRow extends React.Component {
  static propTypes = {
    children: React.PropTypes.oneOfType([
      React.PropTypes.element,
      React.PropTypes.array,
    ]),
  };

  render() {
    return (
      <div className="form-row">
        {this.props.children}
      </div>
    );
  }
}

export default FormRow;
