import React from 'react';

class Form extends React.Component {
  static propTypes = {
    method: React.PropTypes.string,
    children: React.PropTypes.oneOfType([
      React.PropTypes.element,
      React.PropTypes.array,
    ]),
  };

  static defaultProps = {
    method: 'POST',
  };

  render() {
    const { children, ...rest } = this.props;

    return (
      <form
        className="form"
        {...rest}
      >
        {children}
      </form>
    );
  }
}

export default Form;
