import React from 'react';
import { connect } from 'react-redux';

import DashboardNavigation from 'common/components/dashboard-navigation';
import DashboardMain from 'common/components/dashboard-main';
import { initiateRealtimeConnection } from 'common/actions/realtime';

class DashboardLayout extends React.Component {
  static propTypes = {
    children: React.PropTypes.oneOfType([
      React.PropTypes.element,
      React.PropTypes.array,
    ]),
    dispatch: React.PropTypes.func,
  };

  static requireAuth = true;

  componentDidMount() {
    const { dispatch } = this.props;

    if (window.Primus) {
      dispatch(initiateRealtimeConnection(window.Primus));
    }
  }

  render() {
    return (
      <div className="dashboard">
        <DashboardMain>
          {this.props.children}
        </DashboardMain>
        <DashboardNavigation />
      </div>
    );
  }
}

export default connect()(DashboardLayout);
