function dashboard(state = {
  devices: null,
}, action) {
  switch (action.type) {
    case 'REQUEST_DASHBOARD_DEVICES':
      return state;
    case 'RECEIVE_DASHBOARD_DEVICES':
      return Object.assign({}, state, {
        devices: action.response.data,
      });
    case 'CLEAR_DASHBOARD_DEVICES':
      return Object.assign({}, state, {
        devices: null,
      });
    default:
      return state;
  }
}

export default dashboard;
