function fieldSearchModal(state = {
  filter: null,
  status: null,
  meta: null,
  data: null,
}, action) {
  switch (action.type) {
    case 'RECEIVE_FIELD_SEARCH_MODAL_DATA':
      return Object.assign({}, state, {
        status: action.response.status,
        meta: action.response.meta,
        data: action.response.data,
      });
    case 'CLEAR_FIELD_SEARCH_MODAL_DATA':
      return {
        filter: null,
        status: null,
        meta: null,
        data: null,
      };
    case 'FILTER_FIELD_SEARCH_MODAL_DATA':
      return Object.assign({}, state, {
        filter: action.filter,
      });
    default:
      return state;
  }
}

export default fieldSearchModal;
