import { combineReducers } from 'redux';
import { reducer as formReducer } from 'redux-form';

import auth from 'common/reducers/auth';
import me from 'common/reducers/me';
import request from 'common/reducers/request';
import deviceTypes from 'common/reducers/deviceTypes';
import navigation from 'common/reducers/navigation';
import users from 'common/reducers/users';
import flash from 'common/reducers/flash';
import devices from 'common/reducers/devices';
import fieldSearchModal from 'common/reducers/field-search-modal';
import rules from 'common/reducers/rules';
import data from 'common/reducers/data';
import dashboard from 'common/reducers/dashboard';
import realtime from 'common/reducers/realtime';

export const reducers = combineReducers({
  auth,
  me,
  request,
  deviceTypes,
  navigation,
  users,
  flash,
  devices,
  fieldSearchModal,
  rules,
  data,
  dashboard,
  realtime,
  form: formReducer,
});
