import _ from 'lodash';

function me(state = {
  me: null,
}, action) {
  switch (action.type) {
    case 'REQUEST_CURRENT_USER':
      return _.merge(state, {
        request: {
          isFetching: true,
        },
      });
    case 'RECEIVE_CURRENT_USER':
      return action.user;
    default:
      return state;
  }
}

export default me;
