function navigation(state = {
  isOpen: false,
}, action) {
  switch (action.type) {
    case 'TOGGLE_NAVIGATION':
      return {
        isOpen: !state.isOpen,
      };
    case 'CLOSE_NAVIGATION':
      return {
        isOpen: false,
      };
    default:
      return state;
  }
}

export default navigation;
