function realtime(state = {
  connection: null,
}, action) {
  switch (action.type) {
    case 'OPEN_REALTIME_CONNECTION':
      return Object.assign({}, {
        connection: action.connection,
      });
    case 'CLOSE_REALTIME_CONNECTION':
      return Object.assign({}, {
        connection: null,
      });
    default:
      return state;
  }
}

export default realtime;
