function request(state = {
  activeRequests: 0,
  isFetching: false,
}, action) {
  switch (action.type) {
    case 'START_REQUEST':
      return {
        activeRequests: state.activeRequests + 1,
        isFetching: true,
      };
    case 'END_REQUEST':
      return {
        activeRequests: state.activeRequests > 0 ? state.activeRequests - 1 : 0,
        isFetching: state.activeRequests > 1,
      };
    default:
      return state;
  }
}

export default request;
