export function needsAuthentication(components = [], state) {
  return components.reduce((prev, component) =>
    (component.requireAuth && !state.auth.isAuthenticated),
    false);
}

export function needsAuthorisation(components = [], state) {
  return components.reduce((prev, component) =>
    (component.requireAdmin && (!state.me || state.me.role && state.me.role !== 'admin'))
    , false);
}
