export function needsAuthentication(components, state) {
  if (components) {
    return components.reduce((prev, component) => {
      if (component.requireAuth && !state.auth.isAuthenticated) {
        return true;
      }

      return prev;
    }, false);
  }

  return false;
}

export function needsAuthorisation(components, state) {
  if (components) {
    return components.reduce((prev, component) => {
      if (component.requireAdmin && (!state.me || state.me.role && state.me.role !== 'admin')) {
        return true;
      }

      return prev;
    }, false);
  }

  return false;
}
