import React from 'react';
import { connect } from 'react-redux';

import DashboardHeader from 'common/components/dashboard-header';
import DeviceForm from 'common/forms/device-form';
import { addDevice } from 'common/actions/devices';

class AddDevice extends React.Component {
  static requireAdmin = true;

  static propTypes = {
    dispatch: React.PropTypes.func,
  }

  submitCallback = (data) => {
    const { dispatch } = this.props;

    return dispatch(addDevice({
      data,
    }));
  }

  render() {
    return (
      <div>
        <DashboardHeader title="Add a Device" />
        <DeviceForm cta="Submit" onSubmit={this.submitCallback} />
      </div>
    );
  }
}

export default connect()(AddDevice);
