import React from 'react';
import { connect } from 'react-redux';

import DashboardHeader from 'common/components/dashboard-header';
import UserForm from 'common/forms/user-form';

import { addUser } from 'common/actions/users';

class AddUser extends React.Component {
  static requireAdmin = true;

  static propTypes = {
    dispatch: React.PropTypes.func,
  }

  handleSubmit = (data) => {
    const { dispatch } = this.props;

    return dispatch(addUser({
      data,
    }));
  }

  render() {
    return (
      <div>
        <DashboardHeader title="Add User" />
        <UserForm
          onSubmit={this.handleSubmit}
          cta="Submit"
          showRole
        />
      </div>
    );
  }
}

export default connect()(AddUser);
